        "$:/plugins/Cardo/filters/daysOrEmpty.js": {
            "text": "/*\
title: $:/plugins/Cardo/filters/daysOrEmpty.js
type: application/javascript
module-type: filteroperator

Filter operator that selects tiddlers with a specified date field within a specified date interval. If interval is set to 0, it just passes through to the next filter

\*/
(function(){

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

/*
Export our filter function
*/
exports.daysOrEmpty = function(source,operator,options) {
	var results = [],
		fieldName = operator.suffix || "modified",
		dayInterval = (parseInt(operator.operand,10)||0),
		dayIntervalSign = $tw.utils.sign(dayInterval),
		targetTimeStamp = (new Date()).setHours(0,0,0,0) + 1000*60*60*24*dayInterval,
		isWithinDays = function(dateField) {
			var sign = $tw.utils.sign(targetTimeStamp - (new Date(dateField)).setHours(0,0,0,0));
			return sign === 0 || sign === dayIntervalSign;
		};

  if (dayInterval != 0) {
	if(operator.prefix === "!") {
		targetTimeStamp = targetTimeStamp - 1000*60*60*24*dayIntervalSign;
		source(function(tiddler,title) {
			if(tiddler && tiddler.fields[fieldName]) {
				if(!isWithinDays($tw.utils.parseDate(tiddler.fields[fieldName]))) {
					results.push(title);
				}
			}
		});
	} else {
		source(function(tiddler,title) {
			if(tiddler && tiddler.fields[fieldName]) {
				if (isWithinDays( $tw.utils.parseDate(tiddler.fields[fieldName]))) {
					results.push(title);
				}
			}
		});
	}
	return results;
    } else {
      // Pass everything through to the next step - this filter is being asked to do nothing!
      source(function(tiddler,title) {
        results.push(title);
      }); 
    return results;
    }
};

})();
",
