        "$:/plugins/Cardo/macros/newAgendaItemButton": {
            "created": "20160904135737542",
            "creator": "David Szego",
            "text": "\define setOnMeeting()
<$action-setfield $tiddler={{$:/temp/$(currentTiddler)$/agendaitem}} cardo-onmeeting={{!!cardo-guid}} cardo-category={{!!cardo-category}} />
\end
\define newAgendaItemButton(agendaitem) 
<$button class="newButton" aria-label="Create a new Agenda Item " tooltip="Create a new Agenda Item">N<$action-sendmessage $message="tm-new-tiddler" title="We need to talk..." tags="AgendaItem" cardo-type='agendaitem' icon="$:/plugins/Cardo/images/agendaitem" cardo-guid=<<guid>> cardo-onmeeting={{!!cardo-guid}} cardo-category={{!!cardo-category}} />
</$button>
<$reveal type="nomatch" state="$:/temp/$(currentTiddler)$/agendaitem" text="show">
  <$button class="newButton" aria-label="Add a Team Member" tooltip="Add a Team Member" set="$:/temp/$(currentTiddler)$/agendaitem" setTo="show">E</$button>
</$reveal>
<$reveal type="match" state="$:/temp/$(currentTiddler)$/agendaitem" text="show">
  <$button class="newButton" set="$:/temp/$(currentTiddler)$/agendaitem" setTo="hide">-</$button>
  <$select tiddler="""$:/temp/$(currentTiddler)$/agendaitem""" actions=<<setOnMeeting>> >
    <$list filter="[field:cardo-type[agendaitem]] -[has[cardo-onmeeting]] -[has[draft.of]]  +[sort[]] ">
      <option><<currentTiddler>></option>
    </$list>
  </$select>
</$reveal>
\end",
            "type": "text/vnd.tiddlywiki",
            "title": "$:/plugins/Cardo/macros/newAgendaItemButton",
            "tags": "$:/tags/Macro Cardo",
            "revision": "2",
            "modifier": "David Szego",
            "modified": "20170222010117144",
            "icon": "$:/plugins/Cardo/images/paper",
            "bag": "default"
        },
        "$:/plugins/Cardo/macros/newAttendeeButton": {
            "text": "\define setAttendee()
<$action-listops $tiddler=<<currentTiddler>> $field="cardo-attendees" $subfilter={{$:/temp/$(currentTiddler)$/$meeting$/attendee}} />
\end
\define newAttendeeButton(person) 
<$button class="newButton" aria-label="Create a new Attendee" tooltip="Create a new Attendee">N<$action-sendmessage $message="tm-new-tiddler" title="Look who's coming..." tags="Person" cardo-type='person' icon="$:/plugins/Cardo/images/person" cardo-guid=<<guid>> />
  <$action-listops $tiddler=<<currentTiddler>> $field="cardo-attendees" $subfilter=$person$/>
</$button>
<$reveal type="nomatch" state="""$:/temp/$(currentTiddler)$/$meeting$/attendee""" text="show">
  <$button class="newButton" aria-label="Add an Attendee" tooltip="Add an Attendee" set="""$:/temp/$(currentTiddler)$/$meeting$/attendee""" setTo="show">E</$button>
</$reveal>
<$reveal type="match" state="""$:/temp/$(currentTiddler)$/$meeting$/attendee""" text="show">
  <$button class="newButton" set="""$:/temp/$(currentTiddler)$/$meeting$/attendee""" setTo="hide">-</$button>
  <$select tiddler="""$:/temp/$(currentTiddler)$/$meeting$/attendee""" actions=<<setAttendee>> >
    <$list filter='[field:cardo-type[person]] -[has[draft.of]] +[sort[]]'>
      <option value={{!!cardo-guid}} ><$view field='title'/></option>
    </$list>
  </$select>
</$reveal>
\end",
            "type": "text/vnd.tiddlywiki",
            "title": "$:/plugins/Cardo/macros/newAttendeeButton",
            "tags": "$:/tags/Macro Cardo",
            "revision": "2",
            "modifier": "David Szego",
            "modified": "20170221151424931",
            "icon": "$:/plugins/Cardo/images/paper",
            "creator": "David Szego",
            "created": "20160902212933828",
            "bag": "default"
        },
        "$:/plugins/Cardo/macros/showCategory": {
            "text": "\define setAreas()
<$action-listops $tiddler='$:/state/Cardo/showCategory' $filter="[[Realm]][[Area]] +[tagging[]]" $subfilter="+[tagOrEmpty{$:/state/Cardo/showCategory}] [title{$:/state/Cardo/showCategory}]"
/>
\end
\define showCategory()
<$select class="iconButtonText" tiddler='$:/state/Cardo/showCategory' actions=<<setAreas>> >
    <option value="" style="font-weight:bold;font-style:italic;">All</option>
    <option value="(Unassigned)" style="font-weight:bold;font-style:italic;">(Unassigned)</option>
  <$list filter='[all[shadows+tiddlers]tag[Realm]sort[]] -[title[(Unassigned)]] -[has[draft.of]]'>
    <option class="optionGroup" value=<<currentTiddler>> ><<currentTiddler>></option>
      <$list filter='[all[shadows+tiddlers]tag<currentTiddler>tag[Area]] -[has[draft.of]]'>
        <option class="optionChild" value=<<currentTiddler>> ><<currentTiddler>></option>
      </$list>
  </$list>
</$select>
\end",
            "title": "$:/plugins/Cardo/macros/showCategory",
            "tags": "$:/tags/Macro Cardo",
            "modifier": "David Szego",
            "modified": "20170221161310116",
            "creator": "David Szego",
            "created": "20170212031413042"
        },
        "$:/plugins/Cardo/macros/toggleAttendedCheckbox": {
            "text": "\define toggleAttendedCheckbox(attendeeGUID)
  <$set name="buttonClass" filter="[fieldContains:cardo-attended<attendeeGUID>]" value="checkbox" emptyValue="checkbox off">
    <$list filter="[is[current]] -[fieldContains:cardo-attended<attendeeGUID>]">
      <$button class=<<buttonClass>> aria-label="Attended meeting" tooltip="Attended meeting">
        <$action-listops $tiddler=<<currentTiddler>> $field="cardo-attended" $subfilter="+[append<attendeeGUID>]"/>
        {{$:/plugins/Cardo/images/checkbox}}
      </$button>
    </$list>
    <$list filter="[is[current]] +[fieldContains:cardo-attended<attendeeGUID>]">
      <$button class=<<buttonClass>> aria-label="Attended meeting" tooltip="Attended meeting">
        <$action-listops $tiddler=<<currentTiddler>> $field="cardo-attended" $subfilter="+[remove<attendeeGUID>]"/>
        {{$:/plugins/Cardo/images/checkbox-checked}}
      </$button>
    </$list>
  </$set>
\end",
            "type": "text/vnd.tiddlywiki",
            "title": "$:/plugins/Cardo/macros/toggleAttendedCheckbox",
            "tags": "$:/tags/Macro Cardo",
            "revision": "2",
            "modifier": "David Szego",
            "modified": "20170221194038618",
            "icon": "$:/themes/Cardo/images/paper",
